import { createSlice, current } from "@reduxjs/toolkit"

const initialState={
    workouts:[]
}

const WorkoutSlice =createSlice({
    name:'workouts',
    initialState, 
    reducers:{
        CreateWorkout(state, action){
            state.workouts= [...state.workouts, action.payload]
        },
        SetWorkout(state,action){
            state.workouts= action.payload
        },
        DeleteWorkout(state, action){
            state.workouts= state.workouts.filter(e=>e._id!==action.payload._id)
        },
        UpdateWorkout(state, action){
         const newState = [...state.workouts]
         newState.forEach(e=>{
            if(e._id===action.payload._id){
                return action.payload
            }
            else{
               return e
            }
         }
         )
         state.workouts=newState

        }
    }
})

export const {CreateWorkout, SetWorkout, DeleteWorkout, UpdateWorkout}= WorkoutSlice.actions

export default WorkoutSlice.reducer
