import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  user: null,
};

const UserSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    login(state, action) {
      state.user = action.payload;
    },
    logOut(state, action) {
        state.user= null
    },
  },
});

useE

export const {login, logOut}= UserSlice.actions

export default UserSlice.reducer
