import { createSlice } from "@reduxjs/toolkit";
import {useEffect} from 'react'

const initialState = {
  user: null,
};
useEffect(() => {
}, [input]);

const UserSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    login(state, action) {
      state.user = action.payload;
    },
    logOut(state, action) {
        state.user= null
    },
  },
});



export const {login, logOut}= UserSlice.actions

export default UserSlice.reducer
