import { logOut } from '../Slices/UserSlice'
import { useDispatch } from 'react-redux'

export const useLogout = () => {
    const dispatch = useDispatch()
    const logout=()=>{
        localStorage.removeItem('user')
        dispatch(logOut())
    }
  return {logout}
}
